import "./index.css";
import { auth } from "./backend/firebase-config";
import { useAuthState } from "react-firebase-hooks/auth";
import ChatRoom from "./components/ChatRoom";
import UserSettings from "./components/UserSettings";
import SignIn from "./components/SignIn";
import MainMenu from "./components/MainMenu";
import { useState } from "react";

function App() {
  const [user] = useAuthState(auth);
  const [openMenu, setOpenMenu] = useState(false);
  //console.log(user);
  const screenMap = {
    chat: ChatRoom,
    settings: UserSettings
  }
  const [screen, setScreen] = useState(screenMap.chat);


  return (
    <div className="main-container">
      <header className={`header`}>
        <div className="flex justify-between">
          <h1 className="logo">
            <i className="ri-cloud-fill"></i>CloudChat
          </h1>
          <div className="flex justify-end gap-2">
            {user && (
              <p className="flex items-center gap-1 text-sm font-bold text-yellow-100 select-none">
                <i className="text-green-300 ri-user-3-fill"></i>{" "}
                {user.displayName}
              </p>
            )}
            <i
              className={`ri-settings-4-fill text-3xl hover:cursor-pointer hover:text-yellow-50 
          ${openMenu ? "text-yellow-100" : "text-blue-600"} ${
                !user && "hidden"
              }`}
              onClick={() => setOpenMenu(!openMenu)}
            ></i>
          </div>
        </div>
      </header>
      <MainMenu user={user} setOpenMenu={setOpenMenu} openMenu={openMenu} />
      {user ? <screen /> : <SignIn />}
    </div>
  );
}

export default App;
