import {
  signInWithPopup,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
  signInWithEmailAndPassword,
} from "firebase/auth";
import { auth, provider, db } from "../backend/firebase-config";
import { doc, setDoc, serverTimestamp, } from "firebase/firestore";
import { useState } from "react";

export default function SignIn() {
  const [register, setRegister] = useState({
    email: "",
    username: "",
    password: "",
  });
  const [SignIn, setSignIn] = useState({
    email: "",
    username: "",
    password: "",
  });

  

  const syncAccount = () => {
    const { uid, displayName, email, photoURL } = auth.currentUser;
    const accountRef = doc(db, "accounts", auth.currentUser.uid);
    setDoc(accountRef, {
     uid: uid,
     name: displayName,
     displayName: null,
     email: email,
     photoURL: photoURL,
     lastLogin: serverTimestamp(),
   });
 };


  const signInWithGoogle = async () => {
    await signInWithPopup(auth, provider).then(() => !doc(db, "accounts", auth.currentUser.uid) && syncAccount())
    
  }
  return (
    <>
      <section className="bg-blue-300"></section>
      <button
        className="signin-btn"
        onClick={signInWithGoogle}
      >
        Sign in with Google
      </button>
    </>
  );
}
