import ChatMessage from "./ChatMessage";
import { collection, orderBy, query, limit } from "firebase/firestore";
import { db, auth } from "../backend/firebase-config";
import { useCollectionData } from "react-firebase-hooks/firestore";
import { useState } from "react";

export default function ChatRoom() {
  const messagesRef = collection(db, "messages");
  const q = query(messagesRef, orderBy("createdAt"), limit(25));
  const [messages] = useCollectionData(q, { idField: "id" });

  const [formValue, setFormValue] = useState("");

  const sendMessage = async (e) => {
    e.preventDefault();

    const {uid, photoURL} = auth.currentUser;

  };

  return (
    <>
      <main className="flex flex-col gap-3 px-3 py-2 overflow-y-scroll bg-blue-300 grow scroll-smooth">
        {messages &&
          messages.map((msg) => <ChatMessage key={msg.id} message={msg} />)}
      </main>
      <form
        onSubmit={sendMessage}
        className="flex items-center justify-between gap-2 p-2 bg-blue-100 bg-opacity-25 rounded-b md:text-lg"
      >
        <textarea
          className="p-1 rounded shadow-inner h-14 text-slate-800 grow bg-blue-50"
          placeholder="Say something nice"
          value={formValue}
          onChange={(e) => setFormValue(e.target.value)}
        ></textarea>
        <button
          type="submit"
          className="w-12 h-12 font-bold text-blue-100 bg-blue-500 rounded drop-shadow hover:text-blue-50 active:drop-shadow-sm"
        >
          <i class="ri-send-plane-fill"></i>
        </button>
      </form>
    </>
  );
}
