import {
  useCollectionData,
  useDocumentData,
} from "react-firebase-hooks/firestore";
import Error from "./Error";
import Loading from "./Loading";
import {
  collection,
  orderBy,
  query,
  limitToLast,
  serverTimestamp,
  addDoc,
  doc,
} from "firebase/firestore";
import { useState, useRef, useEffect } from "react";
import { auth, db } from "../backend/firebase-config";

export default function ChatList({
  roomSelection,
  setRoomSelection,
  setOpenMenu,
}) {
  const publicChatsRef = collection(db, "publicChats");
  const publicChatsQ = query(publicChatsRef, orderBy("name"), limitToLast(25));
  const [publicChats, loading, error, snapshot] =
    useCollectionData(publicChatsQ);

  const [blankState, setBlankState] = useState(false);

  if (loading) {
    return (
      <>
        <Loading />
      </>
    );
  } else if (error) {
    return (
      <>
        <Error />
      </>
    );
  } else {
    return (
      <>
      <h1>{blankState}</h1>
        <ul>
          {publicChats.map((chatroom, index) => {
            const roomID =
              snapshot._snapshot.docChanges[index].doc.key.path.segments[6];
            return (
              <li
                className={`px-1 pb-1 rounded ${
                  roomID !== roomSelection &&
                  "hover:cursor-pointer hover:bg-blue-50/50"
                } ${roomID === roomSelection && "bg-blue-50/50 underline"}`}
                key={index}
                onClick={() => {
                  setRoomSelection(roomID);
                  setBlankState(chatroom.name);
                  setOpenMenu(false);
                }}
              >
                <span>{chatroom.name}</span>
              </li>
            );
          })}
        </ul>
      </>
    );
  }
}
