import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { updateProfile } from "firebase/auth";
import { storage, auth } from "../backend/firebase-config";
import { useState } from "react";

export default function UserSettings(props) {
  const types = ["image/jpeg", "image/png"];
  const [file, setFile] = useState(null);
  const [uploadError, setUploadError] = useState(null);

  const handleImage = (e) => {
    let selected = e.target.files[0];

    if (selected && types.includes(selected.type)) {
      setFile(selected);
      setUploadError(null);
    } else {
      setFile(null);
      setUploadError("Please select an image file (png or jpeg)");
    }
  };

  const imageSubmit = () => {
    const imagePath = "users/" + props.user.uid + "/user-image-" + file.name;
    let fileRef = ref(storage, imagePath);
    const uploadTask = uploadBytesResumable(fileRef, file);
    uploadTask.on("state_changed", (snapshot) => {
      const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
      console.log("Upload is " + progress + "% done");
    });

    getDownloadURL(ref(storage, imagePath))
      .then((url) => {
        updateProfile(auth.currentUser, {
          photoURL:
            "gs://chat-tut-d42b0.appspot.com/users/" +
            props.user.uid +
            "/user-image-" +
            file.name,
        })
          .then(() => {
            alert("Profile updated!");
          })
          .catch((error) => {
            alert("An error occured");
          });
      })
      .catch((error) => {
        console.log("Error with getDownloadURL");
      });
  };

  return (
    <>
      <main className="main-box">
        <section className="settings-section">
          <h2 className="blue-heading">Profile Image</h2>
          <img
            src={props.user.photoURL}
            alt="user"
            width="100"
            className="rounded"
          />

          <input type="file" onChange={handleImage} />
          <button type="submit" className="btn" onClick={imageSubmit}>
            Upload Image
          </button>
          {uploadError && (
            <div className="text-red-500 error">{uploadError}</div>
          )}
          {file && <div className="error">{file.name}</div>}
        </section>
        <section className="settings-section">
          <h2 className="blue-heading">Display Name</h2>

          <input
            type="text"
            placeholder={props.user.displayName}
            className="form-input"
          />
          <button type="submit" className="btn">
            Change Display Name
          </button>
        </section>
        <section className="settings-section">
          <h2 className="blue-heading">Email</h2>

          <input
            type="text"
            placeholder={props.user.email}
            className="form-input"
          />
          <button type="submit" className="btn">
            Change Email
          </button>
        </section>
        <section className="settings-section">
          <h2 className="blue-heading">Password</h2>

          <input
            type="text"
            placeholder="Enter Your Current Password"
            className="form-input"
          />
          <input
            type="text"
            placeholder="Enter Your New Password"
            className="form-input"
          />
          <button type="submit" className="btn">
            Change Password
          </button>
        </section>
        <section className="settings-section">
          <h2 className="blue-heading">Delete Account</h2>

          <input
            type="text"
            placeholder="Type DELETE in all caps"
            className="form-input"
          />
          <button type="submit" className="btn">
            Delete Account
          </button>
        </section>
      </main>
    </>
  );
}
