import { auth } from "../backend/firebase-config";
import { signOut } from "firebase/auth";

export default function SignOut({ user, setOpenMenu, openMenu }) {
  return (
    user && (
      <>
        <nav className={`flex flex-col items-end select-none`}>
          <ul
            className={`fixed w-full p-2 text-right bg-blue-400/90 drop-shadow-b border-t-blue-50/50 border-t-2 ${
              !openMenu && "hidden"
            }`}
          >
            <li>
              <button
                className="settings-btn"
                onClick={() => setOpenMenu(false)}
              >
                <i className="ri-user-settings-line"></i> User Settings
              </button>
            </li>
            <li>
              <button
                className="settings-btn"
                onClick={() => setOpenMenu(false)}
              >
                <i className="ri-search-line"></i> Public Chats
              </button>
            </li>
            <li>
              <button
                className="settings-btn"
                onClick={() => setOpenMenu(false)}
              >
                <i className="ri-chat-private-line"></i> Direct Chat
              </button>
            </li>
            <li>
              <button
                className="settings-btn"
                onClick={() => setOpenMenu(false)}
              >
                <i className="ri-chat-new-line"></i> Create Chat
              </button>
            </li>
            <li>
              <button
                className="settings-btn"
                onClick={() => {
                  signOut(auth);
                  setOpenMenu(false);
                }}
              >
                <i className="align-bottom ri-logout-box-line"></i> Sign Out
              </button>
            </li>
          </ul>
        </nav>
      </>
    )
  );
}
