export default function Header(props, {children}) {
  return (
    <header className={`header`}>
      <div className="flex justify-between">
        <h1 className="logo">
          <i className="ri-cloud-fill"></i>CloudChat
        </h1>
        <div className="flex justify-end gap-2">
          {props.user && {children}}
          <i
            className={`text-blue-100 transition text-3xl hover:cursor-pointer hover:text-yellow-100 
          ${props.openMenu ? "ri-close-line" : "ri-menu-5-line"} ${
              !props.user && "hidden"
            }`}
            onClick={() => props.setOpenMenu(!props.openMenu)}
          ></i>
        </div>
      </div>
    </header>
  );
}
