import "./index.css";
import { db, auth } from "./firebase-config";

import { useAuthState } from "react-firebase-hooks/auth";
import { useCollections } from "react-firebase-hooks/firestore";

function App() {
  return (
    <div className="container mx-auto rounded drop-shadow-xl sm:w-5/6 md:w-4/6 lg:w-3/6 h-[95vh] flex flex-col">
      <nav className="flex justify-between p-2 bg-blue-100 bg-opacity-25 rounded-t md:text-lg">
        <h1 className="flex gap-2 text-2xl italic font-black tracking-wide text-blue-100 md:text-3xl">
        <i class="ri-cloud-fill"></i> Cloud Chat
        </h1>
        <button className="px-2 py-1 font-bold text-blue-100 bg-blue-400 rounded drop-shadow hover:text-blue-50 active:drop-shadow-sm">
          Sign Out
        </button>
      </nav>
      <main className="bg-blue-100 grow">
        <div>
          <img src="./images/profile-pic.jpg" alt="user" width="100"/>
          <p>Test text message</p>
        </div>
        </main>
      <form className="flex justify-between gap-2 p-2 bg-blue-100 bg-opacity-25 md:text-lg">
        <textarea rows="1" className="p-1 rounded shadow-inner text-slate-800 grow bg-blue-50" placeholder="Say something nice"></textarea>
        <button type="submit" className="px-2 py-1 font-bold text-blue-100 bg-blue-400 rounded drop-shadow hover:text-blue-50 active:drop-shadow-sm"><i class="ri-send-plane-fill"></i></button>
      </form>
    </div>
  );
}

export default App;
