import { useState } from "react";
import { auth, googleAuth } from "../../backend/firebase-config";
import { EmailAuthProvider, linkWithCredential, GoogleAuthProvider, reauthenticateWithCredential } from "firebase/auth";
import { updateDoc } from "firebase/firestore";

export default function AddSignin(props) {
  const currentUser = auth.currentUser;
  const { email, uid } = currentUser;
  const accountRef = props.accountRef;

  const [display, setDisplay] = useState(false);

  const [passwordForm, setPasswordForm] = useState("");
  const [passwordCheck, setPasswordCheck] = useState("");
  const [status, setStatus] = useState("");

  const addPassword = async () => {
    const googleCredential = new GoogleAuthProvider();
    const emailCredential = EmailAuthProvider.credential(email, passwordForm);

    setStatus("Reauthenticating...");

    await reauthenticateWithCredential(currentUser, googleCredential)

    setStatus("Checking password...");
    
    if (passwordForm === passwordCheck) {
      setStatus("Creating new login credentials...");
      await linkWithCredential(currentUser, emailCredential)
        .then((userCredential) => {
          setStatus(
            `You can now login with email and password. ${userCredential.user}`
          );
        })
        .catch((error) => {
          setStatus(`${error.code} ${error.message}`);
        });
    } else {
      setStatus("Passwords did not match! Try again.");
      setPasswordForm("");
      setPasswordCheck("");
    }
  };

  return (
    <section className="border-b-2 border-blue-200 settings-section">
      <h2
        className="blue-heading hover:cursor-pointer hover:text-blue-500"
        onClick={() => setDisplay(!display)}
      >
        Create a password
        <i
          className={`ri-arrow-${display ? "up" : "down"}-s-line align-sub`}
        ></i>
      </h2>
      {display && (
        <>
          <input
            type="password"
            placeholder="Create a password"
            className="form-input"
            value={passwordForm}
            onChange={(e) => setPasswordForm(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter your password again"
            className="form-input"
            value={passwordCheck}
            onChange={(e) => setPasswordCheck(e.target.value)}
          />

          <button
            type="submit"
            className="btn"
            disabled={!passwordForm || !passwordCheck}
            onClick={addPassword}
          >
            Submit
          </button>
          <p>{status}</p>
        </>
      )}
    </section>
  );
}
