import {
  useCollectionData,
  useDocumentData,
} from "react-firebase-hooks/firestore";
import Error from "./Error";
import Loading from "./Loading";
import {
  collection,
  orderBy,
  query,
  limitToLast,
  serverTimestamp,
  addDoc,
} from "firebase/firestore";
import { useState, useRef, useEffect } from "react";
import { auth, db } from "../backend/firebase-config";

export default function ChatList() {
  const publicChatsRef = collection(db, "publicChats");
  const publicChatsQ = query(publicChatsRef, orderBy("name"), limitToLast(25));
  const [publicChats, loading, error] = useCollectionData(publicChatsQ);

  if (loading) {
    return (
      <div className="drop-menu-container">
        <Loading />
      </div>
    );
  } else if (publicChats) {
    return (
      <ul className="pt-8 drop-menu-container">
        <li>
          <header className="fixed flex justify-between font-bold text-blue-600 border-b-2 border-blue-50/50">
            <span>Public Chats</span>
            <span>Moderator</span>
          </header>
          {publicChats.map((chatroom) => (
            <li className="flex justify-between px-1 pb-1 rounded hover:cursor-pointer hover:bg-blue-50/50">
              <span>{chatroom.name}</span><span>{chatroom.creator}</span>
            </li>
          ))}
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
          <li>xxx</li>
        </li>
      </ul>
    );
  } else {
    return (
      <div className="drop-menu-container">
        <Error />
      </div>
    );
  }
}
