import {
  useCollectionData,
  useDocumentData,
} from "react-firebase-hooks/firestore";
import Error from "./Error";
import Loading from "./Loading";
import {
  collection,
  orderBy,
  query,
  limitToLast,
  serverTimestamp,
  addDoc,
  doc,
} from "firebase/firestore";
import { useState, useRef, useEffect } from "react";
import { auth, db } from "../backend/firebase-config";

export default function ChatList({
  roomSelection,
  setRoomSelection,
  setOpenMenu,
  setRoomName,
}) {
  const publicChatsRef = collection(db, "publicChats");
  const publicChatsQ = query(publicChatsRef, orderBy("name"), limitToLast(25));
  const [publicChats, loading, error, snapshot] =
    useCollectionData(publicChatsQ);

  const roomMap = publicChats.map((chatroom, index) => {
    const roomID =
      snapshot._snapshot.docChanges[index].doc.key.path.segments[6];
    return (
      <li
        className={`hover:cursor-pointer hover:bg-blue-50/50 rounded px-1
      ${roomID === roomSelection && "underline"}`}
        key={index}
        onClick={() => {
          setRoomSelection(roomID);
          setRoomName(chatroom.name);
          setOpenMenu(false);
        }}
      >
        <span>{chatroom.name}</span>
      </li>
    );
  });

  const [rooms, setRooms] = useState(roomMap);

  useEffect(() => {
    setRooms(roomMap);
  }, [roomSelection]);

  if (loading) {
    return (
      <>
        <Loading />
      </>
    );
  } else if (error) {
    return (
      <>
        <Error />
      </>
    );
  } else {
    return (
      <>
        <ul>{roomMap}</ul>
      </>
    );
  }
}
