import defaultPic from "../images/cloud-fill.png";
import { auth } from "../backend/firebase-config";

export default function ChatMessage(props) {
  const { text, uid, photoURL, displayName } = props.message;

  const messageClass =
    uid === auth.currentUser.uid ? "flex-row-reverse self-end" : "";

  return (
    <>
      <div className={`flex items-center gap-1 ${messageClass}`}>
      <p className="text-blue-900">{displayName || ""}</p>
        
        <div className={`flex flex-col p-1 ${uid === auth.currentUser.uid ? "items-end":"items-start"}`}>
        <img
          src={photoURL || defaultPic}
          alt="user"
          width="45"
          className="rounded"
        />

          <p className="text-slate-900">{text}</p>
        </div>
      </div>
    </>
  );
}
