import "./index.css";
import { auth, db } from "./backend/firebase-config";
import { useAuthState } from "react-firebase-hooks/auth";
import { collection, addDoc, doc } from "firebase/firestore";
import { useDocumentData } from "react-firebase-hooks/firestore";
import ChatRoom from "./components/ChatRoom";
import UserSettings from "./components/UserSettings";
import SignIn from "./components/SignIn";
import MainMenu from "./components/MainMenu";
import { useState, useEffect } from "react";

function App() {
  const [user] = useAuthState(auth);
  const [openMenu, setOpenMenu] = useState(false);

  const accountRef = doc(db, "accounts", user.uid);
  const [account, loading, error] = useDocumentData(accountRef);

  const headerLoading = () => {
    if (loading) {
      return (
        <div className="animate-spin">
          <i className="ri-loader-5-line"></i>
        </div>
      );
    } else if (error) {
      return (
        <p className="text-red-800">
          <i className="ri-error-warning-line"></i> Error Loading Content
        </p>
      );
    } else {
      return (
        <p className="flex items-center gap-1 text-sm font-bold select-none text-blue-50">
          <i className="text-green-300 ri-user-3-fill animate-pulse"></i>{" "}
          {account.userName || account.displayName}
        </p>
      );
    }
  };

  const screenMap = {
    chat: <ChatRoom />,
    settings: <UserSettings user={user} />,
  };

  const [screen, setScreen] = useState(screenMap.chat);

  return (
    <div className="main-container">
      <header className={`header`}>
        <div className="flex justify-between">
          <h1 className="logo">
            <i className="ri-cloud-fill"></i>CloudChat
          </h1>
          <div className="flex justify-end gap-2">
            {headerLoading}
            <i
              className={`text-blue-100 transition text-3xl hover:cursor-pointer hover:text-yellow-100 
          ${openMenu ? "ri-close-line" : "ri-menu-5-line"} ${
                !user && "hidden"
              }`}
              onClick={() => setOpenMenu(!openMenu)}
            ></i>
          </div>
        </div>
      </header>
      <MainMenu
        user={user}
        setOpenMenu={setOpenMenu}
        setScreen={setScreen}
        openMenu={openMenu}
        screenMap={screenMap}
      />
      {user ? screen : <SignIn />}
    </div>
  );
}

export default App;
