import {
  useCollectionData,
  useDocumentData,
} from "react-firebase-hooks/firestore";
import Error from "./Error";
import Loading from "./Loading";
import {
  collection,
  orderBy,
  query,
  limitToLast,
  serverTimestamp,
  addDoc,
  doc,
} from "firebase/firestore";
import { useState, useRef, useEffect } from "react";
import { auth, db } from "../backend/firebase-config";

export default function UserList() {
  const accountsRef = collection(db, "accounts");
  const accountsQ = query(accountsRef, orderBy("name"), limitToLast(25));
  const [accounts, loading, error, snapshot] = useCollectionData(accountsQ);

  if (loading) {
    return <Loading />;
  } else if (error) {
    return <Error />;
  } else {
    return (
      <ul>
        {accounts &&
          accounts.map((account) => {
            return (
              <>
                <li><span>{account.userName || account.name} {account.userName && "(" + account.name + ")"}</span></li>
              </>
            );
          })}
      </ul>
    );
  }
}
