import { useState } from "react";
import { auth, googleAuth } from "../../backend/firebase-config";
import { updateEmail, createUserWithEmailAndPassword } from "firebase/auth";
import { updateDoc } from "firebase/firestore";

export default function AddSignin(props) {
  const currentUser = auth.currentUser;
  const { email, uid } = currentUser;
  const accountRef = props.accountRef;

  const [display, setDisplay] = useState(false);

  const [emailForm, setEmailForm] = useState("");
  const [passwordForm, setPasswordForm] = useState("");
  const [passwordCheck, setPasswordCheck] = useState("")
  const [status, setStatus] = useState("");

  const emailPassSubmit = async () => {
    setStatus("Updating signin credentials...");

    if (passwordForm === passwordCheck) {
      await createUserWithEmailAndPassword(auth, email, passwordForm)
        .then(async (userCredential) => {
          if (userCredential.user) {
            
            
          }
        })
        .catch((error) => {
          setStatus(error.code + " " + error.message);
          setTimeout(() => setStatus(""), 5000);
        });
    } else {
      setStatus("Passwords did not match! Try again.");
      setPasswordForm("")
      setPasswordCheck("")
      setTimeout(() => setStatus(""), 5000);
    }
  };

  return (
    <section className="border-b-2 border-blue-200 settings-section">
      <h2
        className="blue-heading hover:cursor-pointer hover:text-blue-500"
        onClick={() => setDisplay(!display)}
      >
        Email{" "}
        <i
          className={`ri-arrow-${display ? "up" : "down"}-s-line align-sub`}
        ></i>
      </h2>
      {display && (
        <>
          <input
            type="email"
            placeholder={email}
            className="form-input"
            value={emailForm}
            onChange={(e) => {
              setEmailForm(e.target.value);
            }}
          />
          <input
            type="password"
            placeholder="Enter your password"
            className="form-input"
            value={passwordForm}
            onChange={(e) => setPasswordForm(e.target.value)}
          />
          <button
            type="submit"
            className="btn"
            disabled={!emailForm || !passwordForm}
            onClick={emailPassSubmit}
          >
            Change Email
          </button>
          <p>{status}</p>
        </>
      )}
    </section>
  );
}
