import "./index.css";
import { auth, db } from "./backend/firebase-config";
import { useAuthState } from "react-firebase-hooks/auth";
import { collection, addDoc, doc } from "firebase/firestore";
import { useDocumentData } from "react-firebase-hooks/firestore";
import ChatRoom from "./components/ChatRoom";
import UserSettings from "./components/UserSettings";
import SignIn from "./components/SignIn";
import MainMenu from "./components/MainMenu";
import { useState, useEffect } from "react";
import UserDisplay from "./components/UserDisplay";

function App() {
  const [user] = useAuthState(auth);
  const [openMenu, setOpenMenu] = useState(false);

  if (user !== null) {
    user.providerData.forEach((profile) => {
      console.log("Sign-in provider: " + profile.providerId);
      console.log("  Provider-specific UID: " + profile.uid);
      console.log("  Name: " + profile.displayName);
      console.log("  Email: " + profile.email);
      console.log("  Photo URL: " + profile.photoURL);
    });
  }

  const screenMap = {
    chat: <ChatRoom />,
    settings: <UserSettings user={user} />,
  };

  const [screen, setScreen] = useState(screenMap.chat);

  return (
    <div className="main-container">
      <header className={`header`}>
        <div className="flex justify-between">
          <h1 className="logo">
            <i className="ri-cloud-fill"></i>CloudChat
          </h1>
          <div className="flex justify-end gap-2">
            {user && <UserDisplay user={user}/>}
            <i
              className={`text-blue-100 transition text-3xl hover:cursor-pointer hover:text-yellow-100 
          ${openMenu ? "ri-close-line" : "ri-menu-5-line"} ${
                !user && "hidden"
              }`}
              onClick={() => setOpenMenu(!openMenu)}
            ></i>
          </div>
        </div>
      </header>
      <MainMenu
        user={user}
        setOpenMenu={setOpenMenu}
        setScreen={setScreen}
        openMenu={openMenu}
        screenMap={screenMap}
      />
      {user ? screen : <SignIn />}
    </div>
  );
}

export default App;
