import {
    useCollectionData,
    useDocumentData,
  } from "react-firebase-hooks/firestore";
  import Error from "./Error";
  import Loading from "./Loading";
  import {
    collection,
    orderBy,
    query,
    limitToLast,
    serverTimestamp,
    addDoc,
    doc,
  } from "firebase/firestore";
  import { useState, useRef, useEffect } from "react";
  import { auth, db } from "../backend/firebase-config";
import { async } from "@firebase/util";
  
export default function CreateChat({user}) {
  const [createRoomName, setCreateRoomName] = useState("");
  const [createRoomStatus, setCreateRoomStatus] = useState("");

  const newRoomRef = collection(db, `/publicChats/${createRoomName}`)
  const uid = user.uid

  const createRoom = async () => {
    await addDoc(newRoomRef, {
        createdAt: "",
        creator: user
    })
  };

  return (
    <>
      <section className="border-b-2 border-blue-200 settings-section ">
        <h2 className="blue-heading">
          Create Chat
        </h2>

        <input
          type="text"
          placeholder="Enter room name / topic"
          className="form-input"
          value={createRoomName}
          onChange={(e) => setCreateRoomName(e.target.value)}
        />
        <button className="btn" onClick={createRoom} disabled={!createRoomName}>
          Create Chat
        </button>
        <p>{createRoomStatus}</p>
      </section>
    </>
  );
}
