import defaultPic from "../../images/cloud-fill.png";
import { useState } from "react";
import { storage, auth } from "../../backend/firebase-config";
import { updateProfile, updateEmail } from "firebase/auth";
import { updateDoc } from "firebase/firestore";
import { uploadBytesResumable, ref, getDownloadURL } from "firebase/storage";

export default function UpdateEmail(props) {
  const currentUser = auth.currentUser;
  const { uid, displayName, email } = currentUser;
  const accountRef = props.accountRef;
  const account = props.account;

  const [display, setDisplay] = useState(false);

  const [emailForm, setEmailForm] = useState("");
  const [emailStatus, setEmailStatus] = useState("");
  const [passwordForm, setPasswordForm] = useState("");

  const emailSubmit = async () => {
    setEmailStatus("Updating email...");

    await props.reauthenticate(passwordForm);

    await updateEmail(auth.currentUser, emailForm)
      .then(() => {
        updateDoc(accountRef, { email: emailForm }).then(() => {
          setEmailStatus("Email updated: " + emailForm);
          setEmailForm("");
          setPasswordForm("");
          setTimeout(() => setEmailStatus(""), 5000);
        });
      })
      .catch((error) => {
        setEmailStatus("Error updating email. Please try again.");
        setEmailForm("");
        setPasswordForm("");
        setTimeout(() => setEmailStatus(""), 5000);
      });
  };

  return (
    <section className="border-b-2 border-blue-200 settings-section">
      <h2
        className="blue-heading hover:cursor-pointer hover:text-blue-500"
        onClick={() => setDisplay(!display)}
      >
        Email{" "}
        <i
          className={`ri-arrow-${display ? "up" : "down"}-s-line align-sub`}
        ></i>
      </h2>
      {display && (
        <>
          <input
            type="email"
            placeholder={email}
            className="form-input"
            value={emailForm}
            onChange={(e) => {
              setEmailForm(e.target.value);
            }}
          />
          <input
            type="password"
            placeholder="Enter your password"
            className="form-input"
            value={passwordForm}
            onChange={(e) =>
              setPasswordForm(e.target.value)
            }
          />
          <button
            type="submit"
            className="btn"
            disabled={!emailForm || !passwordForm}
            onClick={emailSubmit}
          >
            Change Email
          </button>
          <p>{emailStatus}</p>
        </>
      )}
    </section>
  );
}
