import "./index.css";
import { db, auth } from "./firebase-config";

import { useAuthState } from "react-firebase-hooks/auth";
import { useCollections } from "react-firebase-hooks/firestore";

function App() {
  return (
    <div className="container mx-auto rounded shadow-md sm:w-5/6 md:w-4/6 lg:w-3/6 h-[95vh] flex flex-col">
      <nav className="flex justify-between p-2 bg-opacity-25 rounded-t md:text-lg bg-slate-100">
        <h1 className="flex gap-2 text-2xl italic font-black tracking-wide text-blue-100 md:text-3xl">
        <i class="ri-cloud-fill"></i> Cloud Chat
        </h1>
        <button className="px-2 py-1 font-bold bg-blue-400 rounded shadow-md hover:text-slate-50 active:shadow-sm">
          Sign Out
        </button>
      </nav>
      <main className="bg-slate-100 grow"></main>
      <form className="flex justify-between gap-2 p-2 bg-opacity-25 md:text-lg bg-slate-100">
        <input className="p-1 rounded grow" placeholder="Say something nice" />
        <button type="submit" className="p-1 font-bold bg-blue-400 rounded shadow-md hover:text-slate-50 active:shadow-sm"><i class="ri-send-plane-fill"></i></button>
      </form>
    </div>
  );
}

export default App;
