import { auth } from "../backend/firebase-config";
import { signOut } from "firebase/auth";

export default function DropMenu({
  user,
  setOpenMenu,
  openMenu,
  setScreen,
  screenMap,
}) {
  return (
    user && (
      <>
        <nav className={`select-none`}>
          <div
            className={`drop-menu flex justify-between visibility-transition ${
              openMenu ? "m-fadeIn" : "m-fadeOut"
            }`}
          >
            <ul className="w-1/2 p-2 overflow-x-hidden overflow-y-scroll rounded whitespace-nowrap bg-blue-300/75 max-h-36">
              <li>Chatroom 1 Chatroom 1 Chatroom 1 Chatroom 1 Chatroom  Chatroom Chatroom </li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
              <li>Chatroom 1</li>
              <li>Chatroom 2</li>
              <li>Chatroom 3</li>
            </ul>

            <ul className="text-right ">
              <li>
                <button
                  className="menu-btn"
                  onClick={() => {
                    setScreen(screenMap.settings);
                    setOpenMenu(false);
                  }}
                >
                  <i className="ri-user-settings-line"></i> User Settings
                </button>
              </li>
              <li>
                <button
                  className="menu-btn"
                  onClick={() => {
                    setScreen(screenMap.chat);
                    setOpenMenu(false);
                  }}
                >
                  <i className="ri-search-line"></i> Public Chats
                </button>
              </li>
              <li>
                <button className="menu-btn" onClick={() => setOpenMenu(false)}>
                  <i className="ri-chat-private-line"></i> Direct Chat
                </button>
              </li>
              <li>
                <button className="menu-btn" onClick={() => setOpenMenu(false)}>
                  <i className="ri-chat-new-line"></i> Create Chat
                </button>
              </li>
              <li>
                <button
                  className="menu-btn"
                  onClick={() => {
                    signOut(auth);
                    setOpenMenu(false);
                    setScreen(screenMap.chat);
                  }}
                >
                  <i className="align-bottom ri-logout-box-line"></i> Sign Out
                </button>
              </li>
            </ul>
          </div>
        </nav>
      </>
    )
  );
}

