import { useState } from "react";
export default function CreateChat() {
  const [roomName, setRoomName] = useState("");
  const [createRoomStatus, setCreateRoomStatus] = useState("");

  const createRoom = () => {};

  return (
    <>
      <section className="border-b-2 border-blue-200 settings-section ">
        <h2 className="blue-heading hover:cursor-pointer hover:text-blue-500">
          Create Chat
        </h2>

        <input
          type="text"
          placeholder="Enter room name / topic"
          className="form-input"
          value={roomName}
          onChange={(e) => setRoomName(e.target.value)}
        />
        <button className="btn" onClick={createRoom} disabled={!roomName}>
          Create Chat
        </button>
        <p>{createRoomStatus}</p>
      </section>
    </>
  );
}
