import profilePic from "../images/profile-pic.jpg";
import { auth } from "../backend/firebase-config";

export default function ChatMessage(props) {
  const { text, uid } = props.message;

  const messageClass = uid === auth.currentUser.uid ? "self-end" : "";
  return (
    <>
      <div className={`flex items-center gap-1 ${messageClass}`}>
        <img src={profilePic} alt="user" width="45" className="rounded-full" />
        <p className="p-2 bg-blue-100 rounded-full">Test text message</p>
      </div>
    </>
  );
}
